mapper.update:
update store
set status = 0
where id = #{id};

update member
set status = 0
where id = #{id};

update account
set status = 0
where id = #{id};

update store
set name    = #{name},
    address = #{address},
    remark  = #{remark}
where id = #{id};

update account
set now_money = #{money}
where id = #{id};

mapper.select:
select count(*)
from supplier
where status = 1;

select id, name, phone, address, fax, email, account_id, remark from supplier
status = 1
AND id = #{id}
AND name like CONCAT('%',#{name},'%')
limit #{page}, #{limit};

select count(*) from supplier

    status = 1

        AND id = #{id}


        AND name like CONCAT('%',#{name},'%')


limit #{page}, #{limit};

select count(*)
from account
where status = 1;

select id, name, account_number, now_money, remark, flag
from account

    status = 1

        AND id = #{id}


        AND name like CONCAT('%',#{name},'%')


        AND flag = #{flag}


limit #{page}, #{limit};

select id, name, account_number, now_money, remark, flag
from account
where id = #{id}
  AND status = 1;

select *
from supplier
where id = #{id}
  and status = 1;

select count(*)
from member
where status = 1;

