from store

    status = 1

        AND id = #{id}


        AND name like CONCAT('%',#{name},'%')


        AND address like CONCAT('%',#{address},'%');

select id, name, address, status, remark from store
status = 1
AND id = #{id}
AND name like CONCAT('%',#{name},'%')
AND address like CONCAT('%',#{address},'%')
limit #{page},#{limit};

select count(*)
from account
where status = 1 and flag = 1;

select id, name, address, status, remark
from store
where name = #{name};

